import React, {useState} from "react";

function Form() {

const [click, setClick] = useState("false");

function handleClick(){
  setClick("true");
}


  return(
    <div class = "text">

    <h1 class = "firstline"> WE 'RE </h1>
    <h1 class = "secondline" > COMING <br/> SOON </h1>

    <p> Hello fellow shoppers! We're currently building our new fashion store.
    Add your email below to stay up - to - date with announcements and our launch deals. </p>
    <div class = "wrapper">
    <form class = "" action = "index.html" method = "post">

    <input type = "text" name = "" value = "" placeholder = "Email Address" />

    <button class = "" onClick={handleClick} type = "button" name = "button" >
    <img class= "arrow" src = "images/icon-arrow.svg" />
    </button>

    {click? <p>Thank you for joining us!</p> : "null"}

    </form>
    </div>



    </div>
  );
}

export default Form;
